CREATE TABLE IF NOT EXISTS `plugin_fileleech_access_detail` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `site_id` int(11) NOT NULL,  `username` varchar(255) COLLATE utf8_bin NOT NULL,  `password` varchar(255) COLLATE utf8_bin NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_bin ;CREATE TABLE IF NOT EXISTS `plugin_fileleech_download` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `site_id` int(11) NOT NULL,  `file_url` text COLLATE utf8_bin NOT NULL,  `filesize` int(11) NOT NULL DEFAULT '0',  `user_id` int(11) DEFAULT NULL,  `user_ip_address` varchar(15) COLLATE utf8_bin NOT NULL,  `date_download` datetime NOT NULL,  PRIMARY KEY (`id`),  KEY `site_id` (`site_id`,`user_id`),  KEY `filesize` (`filesize`),  KEY `user_id` (`user_id`),  KEY `date_download` (`date_download`),  KEY `user_ip_address` (`user_ip_address`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_bin ;CREATE TABLE IF NOT EXISTS `plugin_fileleech_site` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `site_name` varchar(255) COLLATE utf8_bin NOT NULL,  `site_url` varchar(255) COLLATE utf8_bin NOT NULL,  `min_account_type` varchar(10) COLLATE utf8_bin NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=73 ;