CREATE TABLE IF NOT EXISTS `plugin_mediaconverter_queue` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `file_id` int(11) NOT NULL,  `status` enum('pending','processing','completed','failed') COLLATE utf8_bin NOT NULL DEFAULT 'pending',  `date_added` datetime NOT NULL,  `date_started` datetime NOT NULL,  `date_finished` datetime NOT NULL,  `notes` text COLLATE utf8_bin NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=1 ;CREATE TABLE `plugin_mediaconverter_watermark` (`id` INT(11) NOT NULL AUTO_INCREMENT PRIMARY KEY, `file_name` VARCHAR(255) NOT NULL, `image_content` BLOB NOT NULL) ENGINE = MyISAM;ALTER TABLE `plugin_mediaconverter_queue` CHANGE `status` `status` ENUM( 'pending', 'processing', 'completed', 'failed', 'reload', 'cancelled' ) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT 'pending';ALTER TABLE  `plugin_mediaconverter_queue` ADD  `convert_source` ENUM(  'auto',  'user',  'admin' ) NOT NULL DEFAULT  'auto';ALTER TABLE  `plugin_mediaconverter_queue` ADD  `additional_data` TEXT NULL;