tf8_bin NOT NULL,  `admin_payment_link` text COLLATE utf8_bin NOT NULL,  `is_enabled` int(1) NOT NULL DEFAULT '0',  PRIMARY KEY (`id`),  UNIQUE KEY `name_key` (`name_key`),  KEY `is_enabled` (`is_enabled`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=4 ;ALTER TABLE  `plugin_reward_affiliate_id` ADD  `outpayment_method` VARCHAR( 50 ) NOT NULL DEFAULT  'paypal';ALTER TABLE  `plugin_reward_affiliate_id` ADD  `method_data_json` TEXT NULL DEFAULT NULL;UPDATE plugin_reward_affiliate_id SET method_data_json = CONCAT('{"paypal_email":["', paypal_email, '"]}');ALTER TABLE  `plugin_reward_affiliate_id` DROP  `paypal_email`;ALTER TABLE  `plugin_reward_ppd_detail` CHANGE  `status`  `status` ENUM(  'pending',  'cleared',  'cancelled',  'aggregated',  'ip_limit_reached',  'file_limit_reached',  'user_limit_reached' ) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT  'pending';ALTER TABLE  `plugin_reward_ppd_detail` ADD INDEX (  `download_date` );ALTER TABLE  `plugin_reward_ppd_detail` ADD INDEX (  `file_id` );CREATE TABLE IF NOT EXISTS `plugin_reward_ppd_complete_download` (`id` INT(11) NOT NULL AUTO_INCREMENT PRIMARY KEY, `download_token` VARCHAR(64) NOT NULL, `date_added` DATETIME NOT NULL, `download_ip` VARCHAR(45) NOT NULL, `bytes_sent` BIGINT(15) NOT NULL, INDEX (`download_token`)) ENGINE = MyISAM;ALTER TABLE `plugin_reward_ppd_complete_download` ADD INDEX ( `date_added` );ALTER TABLE `plugin_reward_ppd_complete_download` ADD `pay_ppd` INT( 1 ) NOT NULL DEFAULT '0';ALTER TABLE `plugin_reward_ppd_complete_download` ADD INDEX ( `pay_ppd` );CREATE TABLE IF NOT EXISTS `plugin_reward_ppd_group_rate` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `group_id` int(11) NOT NULL,  `range_id` int(11) NOT NULL,  `payout_rate` float(5,2) NOT NULL,  PRIMARY KEY (`id`),  KEY `range_id` (`range_id`),  KEY `group_id` (`group_id`),  KEY `payout_rate` (`payout_rate`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=29;